mapper.select:
 			SELECT BOOKNUM, BOOKNAME, BOOKRATE, BOOKSALE FROM 
			(SELECT  ROWNUM R, B.* FROM
				(
				SELECT * FROM BANKBOOK 





				ORDER BY BOOKNUM DESC
				) B 
			)
		WHERE R BETWEEN #{startRow} AND #{lastRow}

mapper.update:
UPDATE BANKBOOK SET
BOOKNAME=#{bookName}, 
BOOKRATE=#{bookRate},
BOOKSALE=#{bookSale},
BOOKCONTENTS=#{bookContents}
WHERE BOOKNUM=#{bookNum}
